import React, { useEffect, useRef, useState } from 'react';
import './styles/cameraTry.css'

function CameraTry() {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [isCameraOn, setIsCameraOn] = useState(false);

    useEffect(() => {
        if (isCameraOn) {
            startCamera();
        } else {
            stopCamera();
        }
    }, [isCameraOn]);

    const startCamera = () => {
        navigator.mediaDevices.getUserMedia({ video: true })
            .then(stream => {
                videoRef.current.srcObject = stream;
                videoRef.current.play();
            })
            .catch(err => {
                console.error("Error accessing the camera: ", err);
            });
    };

    const stopCamera = () => {
        if (videoRef.current && videoRef.current.srcObject) {
            videoRef.current.srcObject.getTracks().forEach(track => track.stop());
        }
    };

    const applyMakeupEffect = () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;
        const context = canvas.getContext('2d');

        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        // Example: Add a simple lipstick effect
        context.beginPath();
        context.arc(canvas.width / 2, canvas.height / 1.8, 50, 0, Math.PI * 2, true);
        context.fillStyle = 'rgba(255, 0, 0, 0.5)';
        context.fill();

        // Example: Add more makeup effects here using context drawing methods
    };

    return (
        <>
        <div className="image-editor">
      <div {...getRootProps()} className="dropzone">
        <img src={img6} className='uploadimg' alt="Upload" />
        <input {...getInputProps()} />
        {image ? (
          <img src={image} alt="Uploaded" className="uploaded-image" />
        ) : (
          <p className="dropzone-text">Drag & drop an image here, or click to select one</p>
        )}
      </div>

      {image && (
        <button className="analyze-button" onClick={handleAnalyze}>
          Analyze
        </button>
      )}

      {showComparison && previousImage && (
        <div className="comparison-section">
          <h3>Comparison</h3>
          <div className="comparison-images">
            <div>
              <h4>Previous Image</h4>
              <img src={previousImage} alt="Previous" className="comparison-image" />
            </div>
            <div>
              <h4>New Image</h4>
              <img src={image} alt="Current" className="comparison-image" />
            </div>
          </div>
        </div>
      )}
    </div>
        <div className="camera-try-container">
            <h2>Try On Makeup Live!</h2>
            <div className="video-container">
                <video ref={videoRef} className="video-feed"></video>
                <canvas ref={canvasRef} className="makeup-overlay"></canvas>
            </div>
            <div className="controls">
                <button onClick={() => setIsCameraOn(!isCameraOn)}>
                    {isCameraOn ? 'Turn Off Camera' : 'Turn On Camera'}
                </button>
                {isCameraOn && (
                    <button onClick={applyMakeupEffect}>
                        Apply Makeup
                    </button>
                )}
            </div>
        </div>
        </>
    );
}

export default CameraTry;
