import React from 'react';
import Navbar from '../components/Navbar';
import featureImage1 from '../img/img (2).png';
import featureImage2 from '../img/img (4).png';
import img7 from "../img/gifs (7).svg";
import styles from './styles/FeaturesPage.module.css';

const featuresList = [
    {
        title: "LIMITLESS SHADES AND FINISHES",
        description: "Experiment with new finishes and play with colors to find the right match for you in just a few clicks.",
        image: featureImage1,
        link: '/shades-try-on'
    },
    {
        title: "ACNE DETECTION",
        description: "Identify acne-prone areas and get tailored recommendations for your skin.",
        image: featureImage2,
        link: '/acne-detection'
    },
    {
        title: "PERSONALIZED RECOMMENDATIONS",
        description: "Get products and routines based on your unique skin profile.",
        image: featureImage1,
        link: '/personalized-recommendations'
    },
    {
        title: "AI-POWERED ANALYSIS",
        description: "Leverage AI to analyze your skin health and suggest improvements.",
        image: featureImage2,
        link: '/ai-powered-analysis'
    }
];

const FeaturesPage = () => {
    return (
        <>
            <Navbar />
            <div className="container my-5">
                <div className="text-center mb-5">
                    <img src={img7} alt="Header Graphic" className="img-fluid mb-3" style={{ maxWidth: '200px' }} />
                    <h2 className="font-weight-bold mb-2">Our Features</h2>
                    <p className="lead text-muted">Discover the perfect blend of beauty and technology.</p>
                </div>
                <div className="row">
                    {featuresList.map((feature, index) => (
                        <div key={index} className={`col-md-12 mb-4 ${index % 2 === 0 ? 'd-flex flex-row-reverse' : 'd-flex'}`}>
                            <div className="d-flex flex-wrap align-items-center">
                                <div className="col-md-6 text-center">
                                    <img src={feature.image} alt={`${feature.title} Image`} className="img-fluid" />
                                </div>
                                <div className="col-md-6">
                                    <h3 className="font-weight-bold">{feature.title}</h3>
                                    <p>{feature.description}</p>
                                    <a href={feature.link} className="btn btn-primary">Try It On</a>
                                </div>
                            </div>
                            <hr className="w-100" />
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
};

export default FeaturesPage;
