import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import './styles/Occasion.css';  // Ensure you have a separate CSS file for styling

const EventsList = [
    {
        title: "Marriage Party",
        description: "The inner urge to look as good as the bride, why not when we are here.",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "Cocktail Party",
        description: "Want to go to a cocktail party and look all cocky? Try makeups from here.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    },
    {
        title: "University Look",
        description: "Want to show your ex how charming you have become? Let's slay then, girl!",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "No Makeup Look",
        description: "That elegant look, nothing beats it for sure.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    },
    {
        title: "All Eyes No Me Look",
        description: "Wait, looking for something extra and a little spicy? The wait's over, darling.",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "Acne Detection",
        description: "I am the main course, honey-wala look... why fear when we are here.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    }
];

function Occasions() {
    const [selectedFeature, setSelectedFeature] = useState(null);

    return (
        <>
            <Navbar />
            <div className="occasions-container">
                <h2 className="text-center">Special Occasions</h2>
                <p className="text-center tagline">Choose makeup for special events</p>
                <div className="grid-container">
                    {EventsList.map((feature, index) => (
                        <div
                            key={index}
                            className={`feature-card ${selectedFeature?.title === feature.title ? 'expanded' : ''}`}
                            onClick={() => setSelectedFeature(selectedFeature?.title === feature.title ? null : feature)}
                        >
                            <h3 className="card-title">{feature.title}</h3>
                            {selectedFeature?.title === feature.title && (
                                <div className="feature-details">
                                    <p className="card-text">{selectedFeature.description}</p>
                                    <h4>Products:</h4>
                                    <ul>
                                        {selectedFeature.products.map((product, index) => (
                                            <li key={index}>{product}</li>
                                        ))}
                                    </ul>
                                    <h4>Estimated Cost:</h4>
                                    <p>{selectedFeature.costEstimate}</p>
                                    <a href={selectedFeature.link} className="btn feature-btn">Try It On</a>
                                </div>
                            )}
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default Occasions;
