import React from 'react';
import featureImage1 from '../img/img (2).png'; // Import your images
import featureImage2 from '../img/img (4).png';

const featuresList = [
    {
        title: "LIMITLESS SHADES AND FINISHES",
        description: "Experiment with new finishes and play with colors to find the right match for you in just a few clicks.",
        image: featureImage1,
        link: '/shades-finishes'
    },
    {
        title: "ACNE DETECTION",
        description: "In the mood for epic artistry or a work-from-home glow? Pick your favorite looks from our looks catalog, try it on, and shop!",
        image: featureImage2,
        link: '/acne-detection'
    }
];

const Features = () => {
    return (
        <div className="bg-gray-100 py-12">
            <div className="container mx-auto px-6">
                <h2 className="text-3xl font-bold text-center text-gray-800 mb-8">Our Features</h2>
                <div className="grid gap-8 md:grid-cols-2">
                    {featuresList.map((feature, index) => (
                        <div 
                            className={`flex flex-col ${index % 2 === 0 ? 'md:flex-row' : 'md:flex-row-reverse'} bg-white shadow-md rounded-lg overflow-hidden`}
                            key={index}
                        >
                            <img 
                                src={feature.image} 
                                alt={`${feature.title} Image`} 
                                className="w-full md:w-1/2 object-cover"
                            />
                            <div className="p-6 flex flex-col justify-center">
                                <h3 className="text-2xl font-semibold text-gray-800 mb-4">{feature.title}</h3>
                                <p className="text-gray-600 mb-6">{feature.description}</p>
                                <a 
                                    href={feature.link} 
                                    className="inline-block bg-blue-500 text-white px-6 py-3 rounded hover:bg-blue-400 transition-colors duration-300"
                                >
                                    Try It On
                                </a>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </div>
    );
};

export default Features;
