import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import './styles/Occasions.css';
import CameraTry from '../components/cameraTry';

const EventsList = [
    {
        title: "Marriage Party",
        description: "The inner urge to look as good as the bride, why not when we are here.",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "Cocktail Party",
        description: "Want to go to a cocktail party and look all cocky? Try makeups from here.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    },
    {
        title: "University Look",
        description: "Want to show your ex how charming you have become? Let's slay then, girl!",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "No Makeup Look",
        description: "That elegant look, nothing beats it for sure.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    },
    {
        title: "All Eyes No Me Look",
        description: "Wait, looking for something extra and a little spicy? The wait's over, darling.",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "Acne Detection",
        description: "I am the main course, honey-wala look... why fear when we are here.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    }
];


function Occasions() {
    const [selectedEvent, setSelectedEvent] = useState(null);

    const handleEventClick = (event) => {
        setSelectedEvent(selectedEvent?.title === event.title ? null : event);
    };

    return (
        <>
            <Navbar />
            <div className="occasions-container">
                <h2 className="text-center">Special Occasions</h2>
                <p className="text-center tagline">Choose makeup for special events</p>

                {selectedEvent && (
                    <div className="expanded-event-card">
                        <div className="event-content">
                            <h3 className="card-title">{selectedEvent.title}</h3>
                            <p>{selectedEvent.description}</p>
                            <div className="event-details">
                                <h4>Products:</h4>
                                <ul>
                                    {selectedEvent.products.map((product, index) => (
                                        <li key={index}>{product}</li>
                                    ))}
                                </ul>
                                <h4>Estimated Cost:</h4>
                                <p>{selectedEvent.costEstimate}</p>
                                <a href={selectedEvent.link} className="btn event-btn">Try It On</a>
                            </div>
                            <CameraTry />
                        </div>
                    </div>
                )}

                <div className="grid-container">
                    {EventsList.map((event, index) => (
                        <div
                            key={index}
                            className={`event-card ${selectedEvent?.title === event.title ? 'hidden' : ''}`}
                            onClick={() => handleEventClick(event)}
                        >
                            <h3 className="card-title">{event.title}</h3>
                            <p>{event.description}</p>
                        </div>
                    ))}
                </div>
            </div>
        </>
    );
}

export default Occasions;
