import React from 'react';
import featureImage1 from '../img/img (2).png';
import featureImage2 from '../img/img (4).png';
import "./styles/Features.css"

const featuresList = [
    {
        title: "LIMITLESS SHADES AND FINISHES",
        description: "Experiment with new finishes and play with colors to find the right match for you in just a few clicks.",
        image: featureImage1,
        link: '/shades-try-on'
    },
    {
        title: "ACNE DETECTION",
        description: "In the mood for epic artistry or a work-from-home glow? Pick your favorite looks from our looks catalog, try it on, and shop!",
        image: featureImage2,
        link: '/acne-detection'
    }
];

const Features = () => {
    return (
        <div className="container">
            <div className="row">
                <div className="col-md-6 text-center">
                    <div className="p-4">
                        <h1 className="display-4">Makeup Remover</h1>
                        <p className="lead">~See Your True Beauty Unveiled</p>

                        <div className="mt-4">
                            <p className="mb-3">Ready to Unveil Your True Self?</p>
                            <button className="btn btn-success btn-lg">Get Started Now</button>
                        </div>
                    </div>
                </div>
                <div className="col-md-6">
                    <img src={img2}/>
                </div>
            </div>
        </div>

    );
};

export default Features;
