import React from 'react';
import Navbar from '../components/Navbar';
import Footer from '../components/Footer';
import Features from '../components/Features';
import HomeContent from '../components/HomeContent';
import Process from '../components/Process';
import Hero from '../img/hero1.png';

function HomePage() {
    return (
        <>
            {/* Navbar */}
            <Navbar />

            {/* Hero Section */}
            <section className="relative overflow-hidden bg-gray-50">
                {/* Background Pulses */}
                <div className="absolute inset-0 flex justify-center items-center z-0">
                    <div className="bgPulse flex space-x-2">
                        {[...Array(10)].map((_, i) => (
                            <span
                                key={i}
                                className="block bg-blue-500 rounded-full opacity-50 w-10 h-10 animate-pulse"
                            ></span>
                        ))}
                    </div>
                </div>

                {/* Main Container */}
                <div className="container mx-auto flex flex-col md:flex-row items-center justify-between p-6 md:p-12 bg-gray-100 z-10 relative">
                    {/* Left Side - Home Content */}
                    <div className="w-full md:w-1/2 p-4">
                        <HomeContent />
                    </div>

                    {/* Right Side - Image with Shadow */}
                    <div className="w-full md:w-1/2 p-4 flex justify-center items-center">
                        <div className="shadow-xl rounded-lg overflow-hidden">
                            <img src={Hero} alt="hero img" className="w-full h-full object-cover" />
                        </div>
                    </div>
                </div>
            </section>

            {/* Features Section */}
            <section className="py-12 bg-white">
                <div className="container mx-auto">
                    <Features />
                </div>
            </section>

            {/* Process Section */}
            <section className="py-12 bg-gray-50">
                <div className="container mx-auto">
                    <Process />
                </div>
            </section>

            {/* Footer */}
            <Footer />
        </>
    );
}

export default HomePage;
