import React from 'react';

const featuresList = [
    {
        title: "LIMITLESS SHADES AND FINISHES",
        description: "Experiment with new finishes and play with colors to find the right match for you in just a few clicks.",
        image: featureImage1,
        link: '/shades-try-on'
    },
    {
        title: "ACNE DETECTION",
        description: "In the mood for epic artistry or a work-from-home glow? Pick your favorite looks from our looks catalog, try it on, and shop!",
        image: featureImage2,
        link: '/acne-detection'
    }
];
function Ocasions() {
    // const events = {"marraige", "party", "cocktail", "nomakeup", "university"};
    // //all hte makeup we have in it you can try on youself 
  return (
    <div>
      {/* //which ever makeup you choose you can alter that makeup products and accordinlgy seehte cost and list  */}
      <div className="container">
            <div className="row">
            <div className="containerFeatures my-5">
                <h2 className="text-center">Our Features</h2>
                <p className="text-center tagline">Discover the perfect blend of beauty and technology.</p>
                <div className="features-grid container">
                {featuresList.map((feature, index) => (
                        <div className="feature-card" key={index}>
                            <img src="" className="feature-img" alt={`${feature.title} Image`} />
                            <div className="card-body">
                                <h3 className="card-title">{feature.title}</h3>
                                <p className="card-text">{feature.description}</p>
                                <a href={feature.link} className="btn feature-btn">Try It On</a>
                            </div>
                        </div>
                    ))}
                    
                </div>
            </div>
            </div>
        </div>
    </div>
  );
}

export default Ocasions;
