import React from 'react';
import featureImage1 from '../img/img (2).png';
import featureImage2 from '../img/img (4).png';
import "./styles/Features.css"
const featuresList = [
    {
        title: "LIMITLESS SHADES AND FINISHES",
        description: "Experiment with new finishes and play with colors to find the right match for you in just a few clicks.",
        image: featureImage1,
        link: '/shades-finishes'
    },
    {
        title: "ACNE DETECTION",
        description: "In the mood for epic artistry or a work-from-home glow? Pick your favorite looks from our looks catalog, try it on, and shop!",
        image: featureImage2,
        link: '/acne-detection'
    }
];

const Features = () => {
    return (
        <section>
            <div className="container my-5">
                <h2 className="text-center">Our Features</h2>
                <p className="text-center tagline">Discover the perfect blend of beauty and technology.</p>
                <div className="row">
                    {featuresList.map((feature, index) => (
                        <div className="col-md-6 mb-4" key={index}>
                            <div className="card feature-card">
                                <img src={feature.image} className="card-img-top feature-img" alt={`${feature.title} Image`} />
                                <div className="card-body">
                                    <h3 className="card-title">{feature.title}</h3>
                                    <p className="card-text">{feature.description}</p>
                                    <a href={feature.link} className="btn feature-btn">Try It On</a>
                                </div>
                            </div>
                        </div>
                    ))}
                </div>
            </div>
        </section>
    );
};

export default Features;
