import React, { useState } from 'react';
import Navbar from '../components/Navbar';

const EventsList = [
    {
        title: "LIMITLESS SHADES AND FINISHES",
        description: "Experiment with new finishes and play with colors to find the right match for you in just a few clicks.",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "ACNE DETECTION",
        description: "In the mood for epic artistry or a work-from-home glow? Pick your favorite looks from our catalog, try it on, and shop!",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    }
];

function Occasions() {
    const [selectedFeature, setSelectedFeature] = useState(null);

    return (
        <>
        <Navbar/>
        <div>
            <div className="row">
                <div className="containerFeatures my-5">
                    <h2 className="text-center">Special Ocassions</h2>
                    <p className="text-center tagline">choose makeup for special events</p>
                    <div className=" d-flex">
                        <div className="features-list">
                            {EventsList.map((feature, index) => (
                                <div
                                    className="feature-card"
                                    key={index}
                                    onClick={() => setSelectedFeature(feature)}
                                    style={{
                                        cursor: 'pointer',
                                        border: selectedFeature?.title === feature.title ? '2px solid #000' : '1px solid #ccc',
                                        padding: '20px',
                                        marginBottom: '10px'
                                    }}
                                >
                                    <h3 className="card-title">{feature.title}</h3>
                                </div>
                            ))}
                        </div>

                        <div className="feature-details">
                            {selectedFeature ? (
                                <div className="card-body">
                                    <h3 className="card-title">{selectedFeature.title}</h3>
                                    <p className="card-text">{selectedFeature.description}</p>
                                    <h4>Products:</h4>
                                    <ul>
                                        {selectedFeature.products.map((product, index) => (
                                            <li key={index}>{product}</li>
                                        ))}
                                    </ul>
                                    <h4>Estimated Cost:</h4>
                                    <p>{selectedFeature.costEstimate}</p>
                                    <a href={selectedFeature.link} className="btn feature-btn">Try It On</a>
                                </div>
                            ) : (
                                <p>Please select a feature to see the details.</p>
                            )}
                        </div>
                    </div>
                </div>
            </div>
        </div>
        </>
    );
}

export default Occasions;
