import React, { useEffect, useRef, useState } from 'react';
import { useDropzone } from 'react-dropzone';
import './styles/cameraTry.css';
import './styles/ImageEditor.css';
import img6 from "../img/gifs (6).svg";

function CameraTry() {
    const videoRef = useRef(null);
    const canvasRef = useRef(null);
    const [isCameraOn, setIsCameraOn] = useState(false);
    const [image, setImage] = useState(null);

    const { getRootProps, getInputProps } = useDropzone({
        accept: 'image/*',
        onDrop: (acceptedFiles) => {
            const file = acceptedFiles[0];
            const reader = new FileReader();
            reader.onload = () => {
                setImage(reader.result);
                setIsCameraOn(false); // Turn off camera if an image is uploaded
            };
            reader.readAsDataURL(file);
        }
    });

    useEffect(() => {
        if (isCameraOn) {
            startCamera();
        } else {
            stopCamera();
        }
    }, [isCameraOn]);

    const startCamera = () => {
        navigator.mediaDevices.getUserMedia({ video: true })
            .then(stream => {
                videoRef.current.srcObject = stream;
                videoRef.current.play();
            })
            .catch(err => {
                console.error("Error accessing the camera: ", err);
            });
    };

    const stopCamera = () => {
        if (videoRef.current && videoRef.current.srcObject) {
            videoRef.current.srcObject.getTracks().forEach(track => track.stop());
        }
    };

    const applyMakeupEffect = () => {
        const canvas = canvasRef.current;
        const video = videoRef.current;
        const context = canvas.getContext('2d');

        canvas.width = video.videoWidth;
        canvas.height = video.videoHeight;

        context.drawImage(video, 0, 0, canvas.width, canvas.height);

        // Example: Add a simple lipstick effect
        context.beginPath();
        context.arc(canvas.width / 2, canvas.height / 1.8, 50, 0, Math.PI * 2, true);
        context.fillStyle = 'rgba(255, 0, 0, 0.5)';
        context.fill();

        // Example: Add more makeup effects here using context drawing methods
    };

    return (
        <div>
            <div {...getRootProps()} className="cameraTryContainer">
             
                <img src={img6} alt="Uploaded" className="uploaded-image" />
                
                {isCameraOn ? (
                <div className="camera-try-container">
                    <div className="video-container">
                        <video ref={videoRef} className="video-feed"></video>
                        <canvas ref={canvasRef} className="makeup-overlay"></canvas>
                    </div>
                    <button className="btn stop-camera-btn" onClick={() => setIsCameraOn(false)}>
                        Turn Off Camera
                    </button>
                </div>
            ) : (
                <button className="nav-link btn-login" onClick={() => setIsCameraOn(true)}>
                    Turn On Camera
                </button>
                
            )}
            </div>

            
        </div>
    );
}

export default CameraTry;
