import React, { useState } from 'react';
import Navbar from '../components/Navbar';
import './styles/Occasions.css';  // Ensure you have a separate CSS file for styling
import CameraTry from '../components/cameraTry';

const EventsList = [
    {
        title: "Marriage Party",
        description: "The inner urge to look as good as the bride, why not when we are here.",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "Cocktail Party",
        description: "Want to go to a cocktail party and look all cocky? Try makeups from here.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    },
    {
        title: "University Look",
        description: "Want to show your ex how charming you have become? Let's slay then, girl!",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "No Makeup Look",
        description: "That elegant look, nothing beats it for sure.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    },
    {
        title: "All Eyes No Me Look",
        description: "Wait, looking for something extra and a little spicy? The wait's over, darling.",
        link: '/shades-try-on',
        products: ["Lipstick", "Eyeshadow", "Blush"],
        costEstimate: "$50 - $150",
    },
    {
        title: "Acne Detection",
        description: "I am the main course, honey-wala look... why fear when we are here.",
        link: '/acne-detection',
        products: ["Foundation", "Concealer", "Moisturizer"],
        costEstimate: "$30 - $100",
    }
];
function Occasions() {
    const [selectedEvent, setSelectedEvent] = useState(null);
    const [makeupApplied, setMakeupApplied] = useState(null);
    const [showCamera, setShowCamera] = useState(false);

    const handleEventClick = (Event) => {
        setSelectedEvent(Event);
        setShowCamera(true);
        setMakeupApplied(null); // Reset makeup applied when a new event is selected
    };

    const handleMakeupSave = (makeupDetails) => {
        setMakeupApplied(makeupDetails);
        setShowCamera(false); // Hide camera after makeup is saved
        saveMakeupToDatabase(selectedEvent, makeupDetails);
    };

    const saveMakeupToDatabase = (event, makeupDetails) => {
        // Mock API call to save makeup to the database
        console.log("Saving makeup details to the database...", {
            event: event.title,
            makeupDetails: makeupDetails
        });
        // Replace the console.log with an actual API call like:
        // axios.post('/api/save-makeup', { event: event.title, makeupDetails });
    };

    return (
        <>
            <Navbar />
            <div className="occasions-container">
                <h2 className="text-center">Special Occasions</h2>
                <p className="text-center tagline">Choose makeup for special events</p>
                <div className="grid-container">
                    {EventsList.map((Event, index) => (
                        <div
                            key={index}
                            className={`event-card ${selectedEvent?.title === Event.title ? 'expanded' : ''}`}
                            onClick={() => handleEventClick(Event)}
                        >
                            <h3 className="card-title">{Event.title}</h3>
                            <p>{Event.description}</p>
                        </div>
                    ))}
                </div>

                {selectedEvent && (
                    <div className="right-panel">
                        {showCamera ? (
                            <CameraTry onSave={handleMakeupSave} />
                        ) : makeupApplied ? (
                            <div className="makeup-details">
                                <h3>{selectedEvent.title} - Applied Makeup</h3>
                                <h4>Products Used:</h4>
                                <ul>
                                    {makeupApplied.products.map((product, index) => (
                                        <li key={index}>{product}</li>
                                    ))}
                                </ul>
                                <p>Estimated Cost: {makeupApplied.costEstimate}</p>
                            </div>
                        ) : (
                            <p>Select an event and try on makeup.</p>
                        )}
                    </div>
                )}
            </div>
        </>
    );
}

export default Occasions;
