import React, { useState } from 'react';
import { useDropzone } from 'react-dropzone';

const ImageEditor = () => {
  const [image, setImage] = useState(null);
  const [acneDetectionApplied, setAcneDetectionApplied] = useState(false);
  const [shadeApplied, setShadeApplied] = useState(false);

  const { getRootProps, getInputProps } = useDropzone({
    accept: 'image/*',
    onDrop: acceptedFiles => {
      const file = acceptedFiles[0];
      const reader = new FileReader();
      reader.onload = () => {
        setImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  });

  const applyAcneDetection = () => {
    // Simulate acne detection
    setAcneDetectionApplied(true);
  };

  const applyShade = () => {
    // Simulate shade try-on
    setShadeApplied(true);
  };

  const resetChanges = () => {
    setAcneDetectionApplied(false);
    setShadeApplied(false);
  };

  return (
    <div className="container py-5">
      <h2 className="text-center mb-4">Try-On & Acne Detection</h2>

      {/* Dropzone for Image Upload */}
      <div
        {...getRootProps()}
        className="dropzone border border-primary rounded p-5 text-center"
        style={{ cursor: 'pointer' }}
      >
        <input {...getInputProps()} />
        {image ? (
          <img src={image} alt="Uploaded" className="img-fluid" />
        ) : (
          <p>Drag 'n' drop an image here, or click to select one</p>
        )}
      </div>

      {/* Controls */}
      {image && (
        <div className="mt-4 text-center">
          <button
            className="btn btn-primary mx-2"
            onClick={applyAcneDetection}
            disabled={acneDetectionApplied}
          >
            Apply Acne Detection
          </button>
          <button
            className="btn btn-secondary mx-2"
            onClick={applyShade}
            disabled={shadeApplied}
          >
            Apply Shade Try-On
          </button>
          <button className="btn btn-danger mx-2" onClick={resetChanges}>
            Reset Changes
          </button>
        </div>
      )}

      {/* Preview Applied Changes */}
      <div className="mt-4 text-center">
        {acneDetectionApplied && <p>Acne Detection applied!</p>}
        {shadeApplied && <p>Shade Try-On applied!</p>}
      </div>
    </div>
  );
};

export default ImageEditor;
