import React, { useState } from 'react';
import "./"
import { useDropzone } from 'react-dropzone';

const ImageEditor = () => {
  const [image, setImage] = useState(null);

  const { getRootProps, getInputProps } = useDropzone({
    accept: 'image/*',
    onDrop: acceptedFiles => {
      const file = acceptedFiles[0];
      const reader = new FileReader();
      reader.onload = () => {
        setImage(reader.result);
      };
      reader.readAsDataURL(file);
    }
  });

  return (
    <div
      {...getRootProps()}
      className="dropzone p-5 text-center"
      style={{
        border: '3px dotted #aa1945', /* Thick dotted border with Rose Red color */
        backgroundColor: '#f9ccd3', /* Light Rose Quartz background */
        borderRadius: '10px',
        cursor: 'pointer'
      }}
    >
      <input {...getInputProps()} />
      {image ? (
        <img src={image} alt="Uploaded" className="img-fluid" />
      ) : (
        <p style={{ color: '#391306' }}>Drag & drop an image here, or click to select one</p> /* Puce text color */
      )}
    </div>
  );
};

export default ImageEditor;
